SCi = cIm2hrCi(double(imread('GenderCi.tiff')));sigma_b = 20;	%std of smoothing filterS_r = double(imread('faceMask.tif'));vecSCi = SCi(eq(S_r,0));ZSCi = ZTransSCi(SCi,[mean(vecSCi(:)),std(vecSCi(:))]);ZSCi = ZSCi.* S_r;P = .05;	%p-valuetC = 2.7; 	%threshold (for Cluster test)Res = StatThresh(ZSCi,p,sigma_b,tC,S_r);background = double(imread('w1H.JPG'));tCi = DiplayRes(Res,background);