SCi = cIm2hrCi(double(imread('GenderFG.tiff')));sigma_b = 20;	%std of smoothing filterS_r = double(imread('facemask.tiff'));S_r = (S_r - min(S_r(:))) / (max(S_r(:))-min(S_r(:)));vecSCi = SCi(eq(S_r,0));Res = ZTransSCi(SCi,mean(vecSCi(:)),std(vecSCi(:)));Res.ZSCi = Res.ZSCi.*S_r;p = .05;	%p-valuetC = 2.7; 	%threshold (for Cluster test)Res = StatThresh(Res,p,sigma_b,tC,S_r);background = double(imread('w1H.JPG'));tCi = DiplayRes(Res,background);